package com.wadaro.surveyor.base;import android.app.Activity;import android.content.Context;import android.content.ContextWrapper;import android.util.AttributeSet;import android.view.LayoutInflater;import android.widget.RelativeLayout;public abstract class MyLayout extends RelativeLayout {    protected Activity mActivity;    protected String TAG = "MyLayout";    public MyLayout(Context context, AttributeSet attrs) {        super(context, attrs);        LayoutInflater.from(context).inflate(setlayout(),this,true);        mActivity = scanForActivity(context);        TAG = this.getClass().getSimpleName();        initLayout();        initListener();        initData();    }    protected abstract int setlayout();    protected abstract void initLayout();    protected abstract void initListener();    protected void initData(){    }    protected Activity scanForActivity(Context context) {        if (context == null)            return null;        else if (context instanceof Activity)            return (Activity)context;        else if (context instanceof ContextWrapper)            return scanForActivity(((ContextWrapper)context).getBaseContext());        return null;    }}